<?php 	get_header();	global $children;	$term 		= get_queried_object(); 	$taxonomy 	= $term->taxonomy;	$term_id 	= $term->term_id;	$cat_content = get_field('content', $taxonomy.'_'.$term_id); 	$children = get_terms( $taxonomy, array('parent' => $term_id, 'hide_empty' => false) );?><div class="container">	<?php if($cat_content):?>		<article id="content">			<h1><?php single_cat_title();?></h1>			<?php echo $cat_content;?>		</article>	<?php endif;?>	<ul class="posts clearfix">		<?php if(have_posts()):?>			<?php while(have_posts()) : the_post(); ?>				<li class="post_<?php echo $post->ID;?>">					<?php edit_post_link('ערוך', '<div class="edit">', '</div>'); ?>					<a href="<?php the_permalink();?>">						<div class="thumbnail">							<?php post_thumb('IMAGE SIZE');?>						</div>						<h5>							<?php the_title();?>						</h5>					</a>				</li>			<?php endwhile;?>		<?php else:?>			<div class="err">				<div class="no_posts_msg"><?php _e('מצטערים, לא נמצאו מאמרים בקטגוריית ', 'theme');?><?php single_cat_title();?>.</div>				<div class="back_to_home"><a href="<?php echo home_url();?>"><?php _e('חזור לדף הבית', 'theme');?></a></div>			</div>		<?php endif;?>	</ul></div>