<?php 	global $children;	$term 		= get_queried_object(); 	$taxonomy 	= $term->taxonomy;	$term_id 	= $term->term_id;	$cat_content = get_field('content', $taxonomy.'_'.$term_id);?><div class="container">	<?php if($cat_content):?>		<article id="content">			<h1><?php single_cat_title();?></h1>			<?php echo $cat_content;?>		</article>	<?php endif;?>	<ul class="posts clearfix">		<?php foreach ($children as $term) :?>			<?php $image = get_field('image', $taxonomy.'_'.$term->term_id);?>			<li class="post_<?php echo $post->ID;?>">				<?php edit_post_link('ערוך', '<div class="edit">', '</div>'); ?>				<a href="<?php echo get_term_link($term);?>">					<div class="thumbnail">						<div class="overlay trans"></div>						<img src="<?php echo $image['sizes']['IMAGE SIZE'];?>" alt="<?php echo $term->name;?>" />					</div>					<h5>						<?php echo $term->name;?>					</h5>				</a>			</li>		<?php endforeach;?>	</ul></div>